import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { TonConnectButton } from '@tonconnect/ui-react';
import { FaRobot, FaDumbbell, FaGamepad, FaDragon, FaWallet, FaCog } from 'react-icons/fa';

const AppContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #0B0B1F 0%, #1B1B3A 100%);
  color: white;
  padding: 0;
  position: relative;
  overflow-x: hidden;

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background-image: 
      radial-gradient(white, rgba(255,255,255,.2) 2px, transparent 4px),
      radial-gradient(white, rgba(255,255,255,.15) 1px, transparent 2px),
      radial-gradient(white, rgba(255,255,255,.1) 2px, transparent 3px);
    background-size: 550px 550px, 350px 350px, 250px 250px;
    background-position: 0 0, 40px 60px, 130px 270px;
    animation: starMove 120s linear infinite;
  }

  @keyframes starMove {
    from { background-position: 0 0, 40px 60px, 130px 270px; }
    to { background-position: 550px 550px, 390px 410px, 380px 520px; }
  }
`;

const Content = styled.div`
  padding: 20px;
  max-width: 800px;
  margin: 0 auto;
  position: relative;
  z-index: 1;
`;

const WelcomeCard = styled.div`
  background: rgba(30, 30, 60, 0.6);
  border-radius: 20px;
  padding: 30px;
  text-align: center;
  margin-bottom: 30px;
  border: 1px solid rgba(88, 175, 255, 0.3);
  box-shadow: 0 0 20px rgba(88, 175, 255, 0.2),
              inset 0 0 20px rgba(88, 175, 255, 0.1);
  position: relative;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 200px;
  gap: 20px;
  backdrop-filter: blur(5px);

  &::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: linear-gradient(
      45deg,
      transparent 0%,
      rgba(88, 175, 255, 0.1) 50%,
      transparent 100%
    );
    animation: shine 4s linear infinite;
    pointer-events: none;
  }

  @keyframes shine {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }
`;

const Title = styled.h1`
  font-size: 2.5rem;
  background: linear-gradient(135deg, #58AFFF 0%, #A6E1FF 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  text-shadow: 0 0 20px rgba(88, 175, 255, 0.5);
  margin: 0;
  position: relative;
  z-index: 1;
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
  margin-top: 30px;
`;

const ActionButton = styled(motion.button)`
  background: rgba(88, 175, 255, 0.1);
  border: 1px solid rgba(88, 175, 255, 0.3);
  border-radius: 15px;
  padding: 20px;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 15px;
  color: #A6E1FF;
  font-size: 1.2rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
  box-shadow: 0 0 15px rgba(88, 175, 255, 0.2);
  backdrop-filter: blur(5px);
  min-height: 180px;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(88, 175, 255, 0.2) 0%, rgba(166, 225, 255, 0.1) 100%);
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 0 20px rgba(88, 175, 255, 0.4),
                inset 0 0 10px rgba(88, 175, 255, 0.2);
    border-color: rgba(88, 175, 255, 0.5);

    &::before {
      opacity: 1;
    }
  }

  &:active {
    transform: translateY(1px);
  }

  svg {
    font-size: 2.5rem;
    color: #58AFFF;
    filter: drop-shadow(0 0 5px rgba(88, 175, 255, 0.5));
  }
`;

const ButtonDescription = styled.p`
  font-size: 0.9rem;
  opacity: 0.8;
  margin: 0;
  text-align: center;
`;

function App() {
  const [activeTab, setActiveTab] = useState('home');

  return (
    <AppContainer>
      <Content>
        <WelcomeCard>
          <Title>AI Pokemons</Title>
          <TonConnectButton />
        </WelcomeCard>

        <Grid>
          <ActionButton
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <FaRobot />
            AI Resources
            <ButtonDescription>Управляйте ресурсами и характеристиками ваших покемонов</ButtonDescription>
          </ActionButton>

          <ActionButton
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <FaDumbbell />
            Training
            <ButtonDescription>Тренируйте и улучшайте навыки ваших покемонов</ButtonDescription>
          </ActionButton>

          <ActionButton
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <FaGamepad />
            Farm Arena
            <ButtonDescription>Участвуйте в сражениях и получайте награды</ButtonDescription>
          </ActionButton>

          <ActionButton
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <FaDragon />
            My Pokemons
            <ButtonDescription>Управляйте своей коллекцией NFT покемонов</ButtonDescription>
          </ActionButton>

          <ActionButton
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <FaWallet />
            Wallet
            <ButtonDescription>Управляйте балансом и транзакциями</ButtonDescription>
          </ActionButton>

          <ActionButton
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <FaCog />
            Settings
            <ButtonDescription>Настройте приложение под себя</ButtonDescription>
          </ActionButton>
        </Grid>
      </Content>
    </AppContainer>
  );
}

export default App;
