import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { TonConnectButton } from '@tonconnect/ui-react';
import { FaRobot, FaDumbbell, FaGamepad, FaDragon, FaWallet, FaCog, FaSeedling, FaGraduationCap } from 'react-icons/fa';

const AppContainer = styled.div`
  min-height: 100vh;
  background: #FF0000;
  padding: 20px 20px 90px;
  position: relative;
  overflow-x: hidden;
`;

const TopBar = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  height: 60px;
  background: #FF0000;
  display: flex;
  align-items: center;
  padding: 10px 20px;
  z-index: 1000;
  border-bottom: 2px solid #CC0000;

  &::before {
    content: '';
    position: absolute;
    left: 20px;
    top: 50%;
    transform: translateY(-50%);
    width: 40px;
    height: 40px;
    background: #fff;
    border-radius: 50%;
    border: 2px solid #333;
    box-shadow: inset 0 0 10px rgba(0,0,0,0.2);
  }

  &::after {
    content: '';
    position: absolute;
    left: 70px;
    top: 50%;
    transform: translateY(-50%);
    display: flex;
    gap: 10px;
  }
`;

const LogoImage = styled.div`
  width: 180px;
  height: 45px;
  background-image: url('/pokemon_logo.png');
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  margin: 0 auto;
  filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.4));
`;

const TopIndicators = styled.div`
  position: absolute;
  top: 20px;
  left: 20px;
  display: flex;
  gap: 10px;
  z-index: 2;

  &::before {
    content: '';
    width: 50px;
    height: 50px;
    background: #7BB7E7;
    border-radius: 50%;
    border: 4px solid white;
    box-shadow: 0 0 0 2px #333;
  }

  &::after {
    content: '';
    display: flex;
    gap: 10px;
  }
`;

const SmallLights = styled.div`
  display: flex;
  gap: 8px;
  margin-left: 10px;
  align-items: center;

  &::before {
    content: '';
    width: 12px;
    height: 12px;
    background: #FF0000;
    border-radius: 50%;
    border: 2px solid #CC0000;
  }

  &::after {
    content: '';
    width: 12px;
    height: 12px;
    background: #FFFF00;
    border-radius: 50%;
    border: 2px solid #CC9900;
  }

  div {
    width: 12px;
    height: 12px;
    background: #00FF00;
    border-radius: 50%;
    border: 2px solid #009900;
  }
`;

const Content = styled.div`
  background: #f0f0f0;
  border-radius: 15px;
  border: 8px solid #fff;
  margin: 60px auto 0;
  max-width: 600px;
  padding: 20px;
  position: relative;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);

  &::before {
    content: '';
    position: absolute;
    top: -12px;
    right: 20px;
    width: 8px;
    height: 8px;
    background: #CC0000;
    border-radius: 50%;
    box-shadow: -15px 0 0 #CC0000;
  }
`;

const MainButtons = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
  margin-top: 20px;
  padding: 0 10px;
`;

const ActionButton = styled.button`
  width: 100%;
  padding: 20px;
  border-radius: 12px;
  border: none;
  background: ${props => props.green ? '#4CAF50' : '#333'};
  color: white;
  font-size: 18px;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 12px;
  cursor: pointer;
  transition: all 0.2s ease;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 12px rgba(0, 0, 0, 0.3);
  }

  &:active {
    transform: translateY(0);
  }

  svg {
    font-size: 24px;
  }
`;

const BottomBar = styled.div`
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  height: 60px;
  background: #CC0000;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0 20px;
  z-index: 1000;
  border-top: 2px solid #990000;

  &::after {
    content: '';
    position: absolute;
    right: 20px;
    top: 50%;
    transform: translateY(-50%);
    width: 30px;
    height: 30px;
    background: #333;
    clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0% 100%);
  }
`;

const Title = styled.h1`
  font-size: 2.5rem;
  color: #FFCB05;
  margin: 0;
  text-align: center;
  font-weight: 900;
  letter-spacing: 2px;
  text-transform: uppercase;
  -webkit-text-stroke: 2px #2A75BB;
  text-shadow: 
    -1px -1px 0 #2A75BB,  
    1px -1px 0 #2A75BB,
    -1px 1px 0 #2A75BB,
    1px 1px 0 #2A75BB,
    0 4px 0 #2A75BB;
  transform: scaleY(1.2);
`;

const BottomButton = styled.button`
  background: none;
  border: none;
  color: white;
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 12px 20px;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.2s ease;
  font-size: 16px;

  &:hover {
    background: rgba(255, 255, 255, 0.1);
  }

  svg {
    font-size: 26px;
  }
`;

function App() {
  const [activeTab, setActiveTab] = useState('home');

  const renderContent = () => {
    switch (activeTab) {
      case 'wallet':
        return (
          <div style={{ padding: '20px', textAlign: 'center' }}>
            <h2>Wallet</h2>
            <div style={{ margin: '20px auto', maxWidth: '300px' }}>
              <TonConnectButton />
            </div>
          </div>
        );
      case 'pokemons':
        return (
          <div style={{ padding: '20px' }}>
            <h2>My Pokemons</h2>
            {/* Содержимое для My Pokemons */}
          </div>
        );
      case 'settings':
        return (
          <div style={{ padding: '20px' }}>
            <h2>Settings</h2>
            {/* Содержимое для Settings */}
          </div>
        );
      default:
        return (
          <MainButtons>
            <ActionButton>
              <FaRobot /> AI Resources
            </ActionButton>
            <ActionButton green>
              <FaSeedling /> Farm Arena
            </ActionButton>
            <ActionButton>
              <FaGraduationCap /> Training
            </ActionButton>
          </MainButtons>
        );
    }
  };

  return (
    <AppContainer>
      <TopBar>
        <LogoImage />
      </TopBar>
      <TopIndicators>
        <SmallLights>
          <div />
        </SmallLights>
      </TopIndicators>
      <Content>
        {renderContent()}
      </Content>
      <BottomBar>
        <BottomButton onClick={() => setActiveTab('wallet')}>
          <FaWallet />
          Wallet
        </BottomButton>
        <BottomButton onClick={() => setActiveTab('pokemons')}>
          <FaDragon />
          My Pokemons
        </BottomButton>
        <BottomButton onClick={() => setActiveTab('settings')}>
          <FaCog />
          Settings
        </BottomButton>
      </BottomBar>
    </AppContainer>
  );
}

export default App;
