import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaWallet, FaList, FaCog } from 'react-icons/fa';

const MenuContainer = styled(motion.div)`
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  background: white;
  padding: 15px 20px;
  display: flex;
  justify-content: space-around;
  align-items: center;
  z-index: 1000;
  box-shadow: 0 -4px 20px rgba(0, 0, 0, 0.1);
  border-top: 2px solid #ff424e;
`;

const MenuItem = styled(motion.div)`
  display: flex;
  flex-direction: column;
  align-items: center;
  color: #666;
  cursor: pointer;
  padding: 8px 16px;
  border-radius: 12px;
  transition: all 0.3s ease;

  &:hover {
    color: #ff424e;
    background: rgba(255, 66, 78, 0.1);
  }
`;

const IconWrapper = styled.div`
  font-size: 24px;
  margin-bottom: 4px;
  color: inherit;
`;

const Label = styled.span`
  font-size: 12px;
  font-weight: 500;
  color: inherit;
`;

const PokemonMenu = ({ onWalletClick, onListClick, onSettingsClick }) => {
  return (
    <MenuContainer
      initial={{ y: 100 }}
      animate={{ y: 0 }}
      transition={{ duration: 0.5, ease: "easeOut" }}
    >
      <MenuItem
        onClick={onWalletClick}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
      >
        <IconWrapper>
          <FaWallet />
        </IconWrapper>
        <Label>Кошелек</Label>
      </MenuItem>

      <MenuItem
        onClick={onListClick}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
      >
        <IconWrapper>
          <FaList />
        </IconWrapper>
        <Label>Покемоны</Label>
      </MenuItem>

      <MenuItem
        onClick={onSettingsClick}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
      >
        <IconWrapper>
          <FaCog />
        </IconWrapper>
        <Label>Настройки</Label>
      </MenuItem>
    </MenuContainer>
  );
};

export default PokemonMenu;
