import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { TonConnectButton } from '@tonconnect/ui-react';
import { FaRobot, FaDumbbell, FaGamepad, FaDragon, FaWallet, FaCog } from 'react-icons/fa';

const AppContainer = styled.div`
  min-height: 100vh;
  background: #1e1e1e url('https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/25.png') center/contain no-repeat fixed;
  color: white;
  padding: 0;
  position: relative;
  overflow-x: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, #ff0000 0%, #cc0000 100%);
    opacity: 0.85;
    z-index: 1;
  }

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSI1MCIgaGVpZ2h0PSI1MCI+CiAgPGNpcmNsZSBjeD0iMjUiIGN5PSIyNSIgcj0iMjAiIGZpbGw9Im5vbmUiIHN0cm9rZT0icmdiYSgyNTUsMjU1LDI1NSwwLjEpIiBzdHJva2Utd2lkdGg9IjIiLz4KPC9zdmc+') repeat;
    opacity: 0.1;
    z-index: 2;
  }
`;

const Content = styled.div`
  padding: 20px;
  max-width: 800px;
  margin: 0 auto;
  position: relative;
  z-index: 3;
`;

const WelcomeCard = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border-radius: 20px;
  padding: 30px;
  text-align: center;
  margin-bottom: 30px;
  border: 2px solid rgba(255, 255, 255, 0.2);
  box-shadow: 0 0 20px rgba(0, 0, 0, 0.3);
  position: relative;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 200px;
  gap: 20px;
  backdrop-filter: blur(10px);

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 5px;
    background: linear-gradient(90deg, #ff0000, #ffffff, #ff0000);
    animation: borderAnimation 2s linear infinite;
  }

  @keyframes borderAnimation {
    0% { background-position: 0% 0%; }
    100% { background-position: 100% 0%; }
  }
`;

const Title = styled.h1`
  font-size: 3rem;
  font-weight: 800;
  color: #ffffff;
  text-shadow: 
    3px 3px 0 #cc0000,
    -1px -1px 0 #cc0000,
    1px -1px 0 #cc0000,
    -1px 1px 0 #cc0000,
    1px 1px 0 #cc0000;
  margin: 0;
  position: relative;
  z-index: 1;
  letter-spacing: 2px;
  transform: skew(-5deg);
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
  margin-top: 30px;
`;

const ActionButton = styled(motion.button)`
  background: rgba(255, 255, 255, 0.1);
  border: none;
  border-radius: 15px;
  padding: 20px;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 15px;
  color: white;
  font-size: 1.2rem;
  font-weight: 600;
  cursor: pointer;
  position: relative;
  overflow: hidden;
  min-height: 180px;
  backdrop-filter: blur(5px);
  border: 2px solid rgba(255, 255, 255, 0.2);
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.2), transparent);
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  &:hover {
    transform: translateY(-2px);
    border-color: rgba(255, 255, 255, 0.4);
    box-shadow: 0 6px 20px rgba(0, 0, 0, 0.3);

    &::before {
      opacity: 1;
    }

    svg {
      transform: scale(1.1);
      filter: drop-shadow(0 0 10px rgba(255, 255, 255, 0.5));
    }
  }

  &:active {
    transform: translateY(1px);
  }

  svg {
    font-size: 2.5rem;
    color: white;
    transition: all 0.3s ease;
  }
`;

const ButtonDescription = styled.p`
  font-size: 0.9rem;
  opacity: 0.8;
  margin: 0;
  text-align: center;
  color: rgba(255, 255, 255, 0.8);
`;

const ConnectButtonWrapper = styled.div`
  width: 100%;
  max-width: 300px;
  margin: 0 auto;

  :deep(.tc-button) {
    background: white !important;
    color: #cc0000 !important;
    border: none !important;
    border-radius: 30px !important;
    padding: 12px 24px !important;
    font-size: 1.1rem !important;
    font-weight: 700 !important;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3) !important;
    transition: all 0.3s ease !important;
    text-transform: uppercase !important;
    letter-spacing: 1px !important;

    &:hover {
      transform: translateY(-2px) !important;
      box-shadow: 0 6px 20px rgba(0, 0, 0, 0.4) !important;
      background: #f8f8f8 !important;
    }

    &:active {
      transform: translateY(1px) !important;
    }
  }
`;

function App() {
  const [activeTab, setActiveTab] = useState('home');

  return (
    <AppContainer>
      <Content>
        <WelcomeCard>
          <Title>AI POKEMONS</Title>
          <ConnectButtonWrapper>
            <TonConnectButton />
          </ConnectButtonWrapper>
        </WelcomeCard>

        <Grid>
          <ActionButton
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <FaRobot />
            AI Resources
            <ButtonDescription>Управляйте ресурсами и характеристиками ваших покемонов</ButtonDescription>
          </ActionButton>

          <ActionButton
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <FaDumbbell />
            Training
            <ButtonDescription>Тренируйте и улучшайте навыки ваших покемонов</ButtonDescription>
          </ActionButton>

          <ActionButton
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <FaGamepad />
            Farm Arena
            <ButtonDescription>Участвуйте в сражениях и получайте награды</ButtonDescription>
          </ActionButton>

          <ActionButton
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <FaDragon />
            My Pokemons
            <ButtonDescription>Управляйте своей коллекцией NFT покемонов</ButtonDescription>
          </ActionButton>

          <ActionButton
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <FaWallet />
            Wallet
            <ButtonDescription>Управляйте балансом и транзакциями</ButtonDescription>
          </ActionButton>

          <ActionButton
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <FaCog />
            Settings
            <ButtonDescription>Настройте приложение под себя</ButtonDescription>
          </ActionButton>
        </Grid>
      </Content>
    </AppContainer>
  );
}

export default App;
