import React, { useState } from 'react';
import styled from 'styled-components';
import { TonConnectButton } from '@tonconnect/ui-react';
import { FaRobot, FaDragon, FaWallet, FaSeedling, FaGraduationCap, FaTools } from 'react-icons/fa';

const AppContainer = styled.div`
  min-height: 100vh;
  background: #FF0000;
  padding: 20px 20px 90px;
  position: relative;
  overflow-x: hidden;
`;

const TopBar = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  height: 60px;
  background: #FF0000;
  display: flex;
  align-items: center;
  padding: 10px 20px;
  z-index: 1000;
  border-bottom: 2px solid #CC0000;

  &::before {
    content: '';
    position: absolute;
    left: 20px;
    top: 50%;
    transform: translateY(-50%);
    width: 40px;
    height: 40px;
    background: #fff;
    border-radius: 50%;
    border: 2px solid #333;
    box-shadow: inset 0 0 10px rgba(0,0,0,0.2);
  }

  &::after {
    content: '';
    position: absolute;
    left: 70px;
    top: 50%;
    transform: translateY(-50%);
    display: flex;
    gap: 10px;
  }
`;

const LogoImage = styled.div`
  width: 180px;
  height: 45px;
  background-image: url('/pokemon_logo.png');
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  margin: 0 auto;
  filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.4));
`;

const Content = styled.div`
  background: #f0f0f0;
  border-radius: 15px;
  border: 8px solid #fff;
  margin: 60px auto 0;
  max-width: 600px;
  padding: 20px;
  position: relative;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);

  &::before {
    content: '';
    position: absolute;
    top: -12px;
    right: 20px;
    width: 8px;
    height: 8px;
    background: #CC0000;
    border-radius: 50%;
    box-shadow: -15px 0 0 #CC0000;
  }
`;

const MainButtons = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
  margin-top: 20px;
  padding: 0 10px;
`;

const ActionButton = styled.button`
  width: 100%;
  padding: 20px;
  border-radius: 12px;
  border: none;
  background: ${props => props.green ? '#4CAF50' : '#333'};
  color: white;
  font-size: 18px;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 12px;
  cursor: pointer;
  transition: all 0.2s ease;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 12px rgba(0, 0, 0, 0.3);
  }

  &:active {
    transform: translateY(0);
  }

  svg {
    font-size: 24px;
  }
`;

const BottomBar = styled.div`
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  height: 60px;
  background: #CC0000;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0 20px;
  z-index: 1000;
  border-top: 2px solid #990000;
`;

const BottomButton = styled.button`
  background: none;
  border: none;
  color: white;
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 12px 20px;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.2s ease;
  font-size: 16px;

  &:hover {
    background: rgba(255, 255, 255, 0.1);
  }

  svg {
    font-size: 26px;
  }
`;

function App() {
  const [activeTab, setActiveTab] = useState('main');

  const renderContent = () => {
    switch (activeTab) {
      case 'main':
        return (
          <MainButtons>
            <ActionButton>
              <FaRobot /> AI Resources
            </ActionButton>
            <ActionButton green>
              <FaSeedling /> Farm Arena
            </ActionButton>
            <ActionButton>
              <FaGraduationCap /> Training
            </ActionButton>
          </MainButtons>
        );
      default:
        return null;
    }
  };

  return (
    <AppContainer>
      <TopBar>
        <LogoImage />
      </TopBar>
      <Content>
        {renderContent()}
      </Content>
      <BottomBar>
        <BottomButton onClick={() => setActiveTab('wallet')}>
          <FaWallet />
          Wallet
        </BottomButton>
        <BottomButton onClick={() => setActiveTab('pokemons')}>
          <FaDragon />
          My Pokemons
        </BottomButton>
        <BottomButton onClick={() => setActiveTab('settings')}>
          <FaTools />
          Settings
        </BottomButton>
      </BottomBar>
    </AppContainer>
  );
}

export default App;
