import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { TonConnectButton } from '@tonconnect/ui-react';
import { FaRobot, FaDumbbell, FaGamepad, FaDragon, FaWallet, FaCog } from 'react-icons/fa';

const AppContainer = styled.div`
  min-height: 100vh;
  background: #FF0000;
  color: white;
  padding: 20px;
  position: relative;
  overflow-x: hidden;
`;

const TopBar = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  height: 60px;
  background: #FF0000;
  display: flex;
  justify-content: center;
  align-items: center;
  border-bottom: 4px solid #CC0000;
  z-index: 1000;
`;

const TopIndicators = styled.div`
  position: absolute;
  top: 20px;
  left: 20px;
  display: flex;
  gap: 10px;
  z-index: 2;

  &::before {
    content: '';
    width: 50px;
    height: 50px;
    background: #7BB7E7;
    border-radius: 50%;
    border: 4px solid white;
    box-shadow: 0 0 0 2px #333;
  }

  &::after {
    content: '';
    display: flex;
    gap: 10px;
  }
`;

const SmallLights = styled.div`
  display: flex;
  gap: 8px;
  margin-left: 10px;
  align-items: center;

  &::before {
    content: '';
    width: 12px;
    height: 12px;
    background: #FF0000;
    border-radius: 50%;
    border: 2px solid #CC0000;
  }

  &::after {
    content: '';
    width: 12px;
    height: 12px;
    background: #FFFF00;
    border-radius: 50%;
    border: 2px solid #CC9900;
  }

  div {
    width: 12px;
    height: 12px;
    background: #00FF00;
    border-radius: 50%;
    border: 2px solid #009900;
  }
`;

const Content = styled.div`
  background: #DDDDDD;
  border-radius: 15px;
  border: 20px solid white;
  margin-top: 80px;
  margin-bottom: 80px;
  padding: 20px;
  position: relative;
  box-shadow: inset 0 0 20px rgba(0, 0, 0, 0.2);
  min-height: calc(100vh - 220px);

  &::before {
    content: '';
    position: absolute;
    top: -25px;
    right: 20px;
    width: 10px;
    height: 10px;
    background: #FF0000;
    border-radius: 50%;
    box-shadow: -15px 0 0 #FF0000;
  }
`;

const MainButtons = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
  max-width: 400px;
  margin: 40px auto;
`;

const ActionButton = styled(motion.button)`
  background: white;
  border: none;
  border-radius: 10px;
  padding: 20px;
  width: 100%;
  display: flex;
  align-items: center;
  gap: 15px;
  color: #333;
  font-size: 1.2rem;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  position: relative;

  &:hover {
    background: #f8f8f8;
    transform: translateY(-2px);
    box-shadow: 0 6px 12px rgba(0, 0, 0, 0.15);
  }

  &:active {
    transform: translateY(1px);
  }

  svg {
    font-size: 1.5rem;
    color: #FF0000;
  }
`;

const BottomBar = styled.div`
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  height: 70px;
  background: white;
  display: flex;
  justify-content: space-around;
  align-items: center;
  border-top: 2px solid #eee;
  padding: 0 20px;
  z-index: 1000;
`;

const BottomButton = styled.button`
  background: none;
  border: none;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 5px;
  color: ${props => props.active ? '#FF0000' : '#666'};
  font-size: 0.8rem;
  cursor: pointer;
  padding: 5px;
  transition: all 0.3s ease;

  svg {
    font-size: 1.5rem;
  }

  &:hover {
    color: #FF0000;
  }
`;

const Title = styled.h1`
  font-size: 2.5rem;
  color: #FFCB05;
  margin: 0;
  text-align: center;
  font-weight: 900;
  letter-spacing: 2px;
  text-transform: uppercase;
  -webkit-text-stroke: 2px #2A75BB;
  text-shadow: 
    -1px -1px 0 #2A75BB,  
    1px -1px 0 #2A75BB,
    -1px 1px 0 #2A75BB,
    1px 1px 0 #2A75BB,
    0 4px 0 #2A75BB;
  transform: scaleY(1.2);
`;

function App() {
  const [activeTab, setActiveTab] = useState('home');

  const renderContent = () => {
    switch (activeTab) {
      case 'wallet':
        return (
          <div style={{ padding: '20px', textAlign: 'center' }}>
            <h2>Wallet</h2>
            <div style={{ margin: '20px auto', maxWidth: '300px' }}>
              <TonConnectButton />
            </div>
          </div>
        );
      case 'pokemons':
        return (
          <div style={{ padding: '20px' }}>
            <h2>My Pokemons</h2>
            {/* Содержимое для My Pokemons */}
          </div>
        );
      case 'settings':
        return (
          <div style={{ padding: '20px' }}>
            <h2>Settings</h2>
            {/* Содержимое для Settings */}
          </div>
        );
      default:
        return (
          <MainButtons>
            <ActionButton whileHover={{ scale: 1.02 }} whileTap={{ scale: 0.98 }}>
              <FaRobot />
              AI Resources
            </ActionButton>

            <ActionButton whileHover={{ scale: 1.02 }} whileTap={{ scale: 0.98 }}>
              <FaGamepad />
              Farm Arena
            </ActionButton>

            <ActionButton whileHover={{ scale: 1.02 }} whileTap={{ scale: 0.98 }}>
              <FaDumbbell />
              Training
            </ActionButton>
          </MainButtons>
        );
    }
  };

  return (
    <AppContainer>
      <TopBar>
        <Title>AI POKEMONS</Title>
      </TopBar>
      <TopIndicators>
        <SmallLights>
          <div />
        </SmallLights>
      </TopIndicators>
      <Content>
        {renderContent()}
      </Content>
      <BottomBar>
        <BottomButton 
          onClick={() => setActiveTab('wallet')}
          active={activeTab === 'wallet'}
        >
          <FaWallet />
          Wallet
        </BottomButton>
        <BottomButton 
          onClick={() => setActiveTab('pokemons')}
          active={activeTab === 'pokemons'}
        >
          <FaDragon />
          My Pokemons
        </BottomButton>
        <BottomButton 
          onClick={() => setActiveTab('settings')}
          active={activeTab === 'settings'}
        >
          <FaCog />
          Settings
        </BottomButton>
      </BottomBar>
    </AppContainer>
  );
}

export default App;
