import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import WebApp from '@twa-dev/sdk';
import { TonConnectButton } from '@tonconnect/ui-react';
import PokemonMenu from './components/PokemonMenu';

const AppContainer = styled.div`
  width: 100%;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: linear-gradient(135deg, #ff424e 0%, #ff6b6b 100%);
  color: white;
  padding-bottom: 80px;
`;

const Header = styled.div`
  width: 100%;
  padding: 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-bottom: 2px solid rgba(255, 255, 255, 0.2);
`;

const Title = styled.h1`
  font-size: 1.8rem;
  font-weight: 700;
  margin: 0;
  color: white;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.2);
`;

const Content = styled.div`
  width: 100%;
  max-width: 600px;
  padding: 20px;
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;
`;

const Card = styled(motion.div)`
  width: 100%;
  background: white;
  border-radius: 20px;
  padding: 20px;
  margin-bottom: 20px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
  border: 2px solid rgba(255, 255, 255, 0.3);
`;

const CardTitle = styled.h2`
  font-size: 1.4rem;
  margin: 0 0 15px 0;
  color: #ff424e;
  text-align: center;
`;

const CardContent = styled.div`
  color: #333;
  text-align: center;
`;

const ConnectButton = styled.div`
  margin-top: 20px;

  // Стилизация кнопки TON Connect
  :deep(.tc-button) {
    background: linear-gradient(135deg, #ff424e 0%, #ff6b6b 100%) !important;
    border: none !important;
    border-radius: 30px !important;
    padding: 12px 24px !important;
    font-size: 1.1rem !important;
    font-weight: 600 !important;
    box-shadow: 0 4px 15px rgba(255, 66, 78, 0.3) !important;
    transition: all 0.3s ease !important;

    &:hover {
      transform: translateY(-2px) !important;
      box-shadow: 0 6px 20px rgba(255, 66, 78, 0.4) !important;
    }

    &:active {
      transform: translateY(1px) !important;
    }
  }
`;

const WelcomeCard = styled(Card)`
  text-align: center;
  background: rgba(255, 255, 255, 0.95);

  h1 {
    font-size: 2rem;
    color: #ff424e;
    margin-bottom: 15px;
  }

  p {
    color: #666;
    margin-bottom: 25px;
    font-size: 1.1rem;
  }
`;

function App() {
  const [activeTab, setActiveTab] = useState('home');

  const handleWalletClick = () => {
    setActiveTab('wallet');
  };

  const handleListClick = () => {
    setActiveTab('pokemons');
  };

  const handleSettingsClick = () => {
    setActiveTab('settings');
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'pokemons':
        return (
          <Card
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <CardTitle>Мои покемоны</CardTitle>
            <CardContent>
              Здесь будет список ваших покемонов
            </CardContent>
          </Card>
        );
      case 'settings':
        return (
          <Card
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <CardTitle>Настройки</CardTitle>
            <CardContent>
              Настройки приложения
            </CardContent>
          </Card>
        );
      default:
        return (
          <WelcomeCard
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <h1>Добро пожаловать в Pokemon TMA!</h1>
            <p>Подключите свой кошелек TON, чтобы начать приключение</p>
            <ConnectButton>
              <TonConnectButton />
            </ConnectButton>
          </WelcomeCard>
        );
    }
  };

  return (
    <AppContainer>
      <Header>
        <Title>Pokemon TMA</Title>
      </Header>

      <Content>
        {renderContent()}
      </Content>

      <PokemonMenu
        onWalletClick={handleWalletClick}
        onListClick={handleListClick}
        onSettingsClick={handleSettingsClick}
      />
    </AppContainer>
  );
}

export default App;
