import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import WebApp from '@twa-dev/sdk';

const AppContainer = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background: #1a1a1a;
  color: white;
`;

const ComingSoonText = styled(motion.h1)`
  font-size: 2.5rem;
  margin-bottom: 2rem;
  text-align: center;
`;

const PokeballContainer = styled(motion.div)`
  width: 100px;
  height: 100px;
  position: relative;
  cursor: pointer;
`;

const PokeballTop = styled(motion.div)`
  width: 100%;
  height: 50%;
  background: #ff0000;
  border-radius: 50px 50px 0 0;
  border: 5px solid #000;
`;

const PokeballBottom = styled(motion.div)`
  width: 100%;
  height: 50%;
  background: white;
  border-radius: 0 0 50px 50px;
  border: 5px solid #000;
`;

const PokeballCenter = styled(motion.div)`
  width: 30px;
  height: 30px;
  background: white;
  border: 5px solid #000;
  border-radius: 50%;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
`;

function App() {
  const [isOpen, setIsOpen] = useState(false);

  const handlePokeballClick = () => {
    setIsOpen(!isOpen);
    WebApp.showAlert('Покебол открылся!');
  };

  return (
    <AppContainer>
      <AnimatePresence>
        <ComingSoonText
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: 50 }}
          transition={{ duration: 1 }}
        >
          Coming Soon
        </ComingSoonText>
      </AnimatePresence>

      <PokeballContainer onClick={handlePokeballClick}>
        <PokeballTop
          animate={{
            rotateX: isOpen ? 180 : 0,
          }}
          transition={{ duration: 0.5 }}
        />
        <PokeballBottom />
        <PokeballCenter
          animate={{
            scale: isOpen ? 1.2 : 1,
          }}
          transition={{ duration: 0.3 }}
        />
      </PokeballContainer>
    </AppContainer>
  );
}

export default App;
