import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import WebApp from '@twa-dev/sdk';
import { TonConnectButton } from '@tonconnect/ui-react';
import PokemonMenu from './components/PokemonMenu';

const AppContainer = styled.div`
  width: 100%;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: linear-gradient(135deg, #1a1a1a 0%, #2a2a2a 100%);
  color: white;
  padding-bottom: 80px;
`;

const Header = styled.div`
  width: 100%;
  padding: 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(10px);
`;

const Title = styled.h1`
  font-size: 1.5rem;
  font-weight: 500;
  margin: 0;
`;

const Content = styled.div`
  width: 100%;
  max-width: 600px;
  padding: 20px;
  flex: 1;
`;

const Card = styled(motion.div)`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 16px;
  padding: 20px;
  margin-bottom: 20px;
  border: 1px solid rgba(255, 255, 255, 0.1);
`;

const CardTitle = styled.h2`
  font-size: 1.2rem;
  margin: 0 0 15px 0;
  color: white;
`;

const CardContent = styled.div`
  color: rgba(255, 255, 255, 0.8);
`;

function App() {
  const [activeTab, setActiveTab] = useState('home');

  const handleWalletClick = () => {
    setActiveTab('wallet');
  };

  const handleListClick = () => {
    setActiveTab('pokemons');
  };

  const handleSettingsClick = () => {
    setActiveTab('settings');
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'wallet':
        return (
          <Card
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <CardTitle>TON Connect</CardTitle>
            <CardContent>
              <TonConnectButton />
            </CardContent>
          </Card>
        );
      case 'pokemons':
        return (
          <Card
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <CardTitle>Мои покемоны</CardTitle>
            <CardContent>
              Здесь будет список ваших покемонов
            </CardContent>
          </Card>
        );
      case 'settings':
        return (
          <Card
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <CardTitle>Настройки</CardTitle>
            <CardContent>
              Настройки приложения
            </CardContent>
          </Card>
        );
      default:
        return (
          <Card
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <CardTitle>Добро пожаловать!</CardTitle>
            <CardContent>
              Используйте меню внизу для навигации
            </CardContent>
          </Card>
        );
    }
  };

  return (
    <AppContainer>
      <Header>
        <Title>Pokemon TMA</Title>
      </Header>

      <Content>
        {renderContent()}
      </Content>

      <PokemonMenu
        onWalletClick={handleWalletClick}
        onListClick={handleListClick}
        onSettingsClick={handleSettingsClick}
      />
    </AppContainer>
  );
}

export default App;
