import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import WebApp from '@twa-dev/sdk';
import { TonConnectButton } from '@tonconnect/ui-react';
import PokemonMenu from './components/PokemonMenu';
import { FaRobot, FaGraduationCap, FaSeedling } from 'react-icons/fa';

const AppContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #0B0B1F 0%, #1B1B3A 100%);
  color: white;
  padding: 0;
  position: relative;
  overflow-x: hidden;

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background-image: 
      radial-gradient(white, rgba(255,255,255,.2) 2px, transparent 4px),
      radial-gradient(white, rgba(255,255,255,.15) 1px, transparent 2px),
      radial-gradient(white, rgba(255,255,255,.1) 2px, transparent 3px);
    background-size: 550px 550px, 350px 350px, 250px 250px;
    background-position: 0 0, 40px 60px, 130px 270px;
    animation: starMove 120s linear infinite;
  }

  @keyframes starMove {
    from { background-position: 0 0, 40px 60px, 130px 270px; }
    to { background-position: 550px 550px, 390px 410px, 380px 520px; }
  }
`;

const Header = styled.header`
  padding: 15px 20px;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  background: transparent;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 100;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  background: linear-gradient(135deg, #58AFFF 0%, #A6E1FF 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  text-shadow: 0 0 20px rgba(88, 175, 255, 0.5);
  margin: 0;
  position: relative;
  z-index: 1;
`;

const Content = styled.div`
  padding: 20px;
  padding-top: 70px;
  max-width: 600px;
  margin: 0 auto;
  position: relative;
  z-index: 1;
`;

const Card = styled(motion.div)`
  width: 100%;
  background: rgba(255, 255, 255, 0.03);
  border-radius: 20px;
  padding: 25px;
  margin-bottom: 20px;
  border: 2px solid #ff0000;
  backdrop-filter: blur(10px);
  position: relative;
  overflow: hidden;
`;

const CardTitle = styled.h2`
  font-size: 1.4rem;
  margin: 0 0 15px 0;
  color: white;
  text-align: center;
  font-weight: 600;
  letter-spacing: 0.5px;
`;

const CardContent = styled.div`
  color: rgba(255, 255, 255, 0.8);
  text-align: center;
  font-size: 1.1rem;
  line-height: 1.5;
`;

const WelcomeCard = styled.div`
  background: rgba(30, 30, 60, 0.6);
  border-radius: 20px;
  padding: 30px;
  text-align: center;
  margin-bottom: 30px;
  border: 1px solid rgba(88, 175, 255, 0.3);
  box-shadow: 0 0 20px rgba(88, 175, 255, 0.2),
              inset 0 0 20px rgba(88, 175, 255, 0.1);
  position: relative;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 200px;
  gap: 20px;
  backdrop-filter: blur(5px);

  &::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: linear-gradient(
      45deg,
      transparent 0%,
      rgba(88, 175, 255, 0.1) 50%,
      transparent 100%
    );
    animation: shine 4s linear infinite;
    pointer-events: none;
  }

  @keyframes shine {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }
`;

const ActionButton = styled(motion.button)`
  background: rgba(88, 175, 255, 0.1);
  border: 1px solid rgba(88, 175, 255, 0.3);
  border-radius: 15px;
  padding: 20px;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 15px;
  color: #A6E1FF;
  font-size: 1.2rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-bottom: 15px;
  position: relative;
  overflow: hidden;
  box-shadow: 0 0 15px rgba(88, 175, 255, 0.2);
  backdrop-filter: blur(5px);

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(88, 175, 255, 0.2) 0%, rgba(166, 225, 255, 0.1) 100%);
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 0 20px rgba(88, 175, 255, 0.4),
                inset 0 0 10px rgba(88, 175, 255, 0.2);
    border-color: rgba(88, 175, 255, 0.5);

    &::before {
      opacity: 1;
    }
  }

  &:active {
    transform: translateY(1px);
  }

  svg {
    font-size: 1.5rem;
    color: #58AFFF;
    filter: drop-shadow(0 0 5px rgba(88, 175, 255, 0.5));
  }
`;

const ConnectButton = styled.div`
  width: 100%;
  max-width: 300px;
  margin: 0 auto;

  :deep(.tc-button) {
    background: linear-gradient(135deg, #FFD700 0%, #FFA500 100%) !important;
    border: none !important;
    border-radius: 30px !important;
    padding: 12px 24px !important;
    font-size: 1.1rem !important;
    font-weight: 600 !important;
    box-shadow: 0 4px 15px rgba(255, 215, 0, 0.3) !important;
    transition: all 0.3s ease !important;
    letter-spacing: 0.5px !important;
    width: 100% !important;

    &:hover {
      transform: translateY(-2px) !important;
      box-shadow: 0 6px 20px rgba(255, 215, 0, 0.4) !important;
    }

    &:active {
      transform: translateY(1px) !important;
    }
  }
`;

const SectionTitle = styled.h2`
  font-size: 1.8rem;
  color: white;
  text-align: center;
  margin: 30px 0 20px;
  font-weight: 600;
  letter-spacing: 1px;
  text-shadow: 0 0 10px rgba(255, 215, 0, 0.3);
`;

const MenuButton = styled.button`
  background: none;
  border: none;
  cursor: pointer;
  padding: 0;
  margin: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
`;

function App() {
  const [activeTab, setActiveTab] = useState('home');

  const handleWalletClick = () => {
    // Обработка клика по кошельку
  };

  const handleListClick = () => {
    setActiveTab('pokemons');
  };

  const handleSettingsClick = () => {
    setActiveTab('settings');
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'pokemons':
        return (
          <Card
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <CardTitle>My Pokemons</CardTitle>
            <CardContent>
              Your Pokemon collection will appear here
            </CardContent>
          </Card>
        );
      case 'settings':
        return (
          <Card
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <CardTitle>Settings</CardTitle>
            <CardContent>
              App settings
            </CardContent>
          </Card>
        );
      default:
        return (
          <>
            <WelcomeCard>
              <Title>AI Pokemons</Title>
              <TonConnectButton />
            </WelcomeCard>

            <ActionButton
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: 0.2 }}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              <FaRobot size={24} />
              AI Resources
            </ActionButton>

            <ActionButton
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: 0.3 }}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              <FaGraduationCap />
              Training
            </ActionButton>

            <ActionButton
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: 0.4 }}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              <FaSeedling />
              Farm Arena
            </ActionButton>
          </>
        );
    }
  };

  return (
    <AppContainer>
      <Header />
      <Content>
        {renderContent()}
      </Content>

      <PokemonMenu
        onWalletClick={handleWalletClick}
        onListClick={handleListClick}
        onSettingsClick={handleSettingsClick}
      />
    </AppContainer>
  );
}

export default App;
