import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import WebApp from '@twa-dev/sdk';
import { TonConnectButton } from '@tonconnect/ui-react';
import PokemonMenu from './components/PokemonMenu';

const AppContainer = styled.div`
  width: 100%;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: #000000;
  color: white;
  padding-bottom: 80px;
`;

const Header = styled.div`
  width: 100%;
  padding: 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: rgba(255, 255, 255, 0.05);
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
`;

const Title = styled.h1`
  font-size: 1.8rem;
  font-weight: 700;
  margin: 0;
  color: white;
  text-shadow: 0 0 10px rgba(255, 255, 255, 0.3);
`;

const Content = styled.div`
  width: 100%;
  max-width: 600px;
  padding: 20px;
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const Card = styled(motion.div)`
  width: 100%;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 16px;
  padding: 20px;
  margin-bottom: 20px;
  border: 1px solid rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
`;

const CardTitle = styled.h2`
  font-size: 1.4rem;
  margin: 0 0 15px 0;
  color: white;
  text-align: center;
`;

const CardContent = styled.div`
  color: rgba(255, 255, 255, 0.8);
  text-align: center;
`;

const ConnectButton = styled.div`
  margin-top: 20px;

  :deep(.tc-button) {
    background: linear-gradient(135deg, #00ff88 0%, #00b4d8 100%) !important;
    border: none !important;
    border-radius: 30px !important;
    padding: 12px 24px !important;
    font-size: 1.1rem !important;
    font-weight: 600 !important;
    box-shadow: 0 4px 15px rgba(0, 255, 136, 0.3) !important;
    transition: all 0.3s ease !important;

    &:hover {
      transform: translateY(-2px) !important;
      box-shadow: 0 6px 20px rgba(0, 255, 136, 0.4) !important;
    }

    &:active {
      transform: translateY(1px) !important;
    }
  }
`;

const WelcomeCard = styled(Card)`
  text-align: center;

  h1 {
    font-size: 2rem;
    color: white;
    margin-bottom: 15px;
    background: linear-gradient(135deg, #00ff88 0%, #00b4d8 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
  }

  p {
    color: rgba(255, 255, 255, 0.8);
    margin-bottom: 25px;
    font-size: 1.1rem;
  }
`;

const ResourceCard = styled(Card)`
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    background: rgba(255, 255, 255, 0.08);
  }
`;

function App() {
  const [activeTab, setActiveTab] = useState('home');

  const handleWalletClick = () => {
    setActiveTab('wallet');
  };

  const handleListClick = () => {
    setActiveTab('pokemons');
  };

  const handleSettingsClick = () => {
    setActiveTab('settings');
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'pokemons':
        return (
          <Card
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <CardTitle>My Pokemons</CardTitle>
            <CardContent>
              Your Pokemon collection will appear here
            </CardContent>
          </Card>
        );
      case 'settings':
        return (
          <Card
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <CardTitle>Settings</CardTitle>
            <CardContent>
              App settings
            </CardContent>
          </Card>
        );
      default:
        return (
          <>
            <WelcomeCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3 }}
            >
              <h1>AI Pokemons</h1>
              <p>Connect your TON wallet to start your AI Pokemon adventure</p>
              <ConnectButton>
                <TonConnectButton />
              </ConnectButton>
            </WelcomeCard>

            <CardTitle>AI Resources</CardTitle>
            <ResourceCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: 0.2 }}
            >
              <CardTitle>Create Pokemon</CardTitle>
              <CardContent>
                Generate your unique AI Pokemon
              </CardContent>
            </ResourceCard>

            <ResourceCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: 0.3 }}
            >
              <CardTitle>Train Pokemon</CardTitle>
              <CardContent>
                Train and evolve your AI Pokemon
              </CardContent>
            </ResourceCard>

            <ResourceCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: 0.4 }}
            >
              <CardTitle>Battle</CardTitle>
              <CardContent>
                Battle with other AI Pokemon trainers
              </CardContent>
            </ResourceCard>
          </>
        );
    }
  };

  return (
    <AppContainer>
      <Header>
        <Title>AI Pokemons</Title>
      </Header>

      <Content>
        {renderContent()}
      </Content>

      <PokemonMenu
        onWalletClick={handleWalletClick}
        onListClick={handleListClick}
        onSettingsClick={handleSettingsClick}
      />
    </AppContainer>
  );
}

export default App;
