import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import WebApp from '@twa-dev/sdk';
import { TonConnectButton } from '@tonconnect/ui-react';
import PokemonMenu from './components/PokemonMenu';
import { FaPlus, FaGraduationCap, FaFistRaised } from 'react-icons/fa';

const AppContainer = styled.div`
  width: 100%;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: #000000;
  color: white;
  padding-bottom: 80px;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 100%;
    background: radial-gradient(circle at 50% 0%, rgba(255, 0, 0, 0.15), transparent 70%);
    pointer-events: none;
  }
`;

const Header = styled.div`
  width: 100%;
  padding: 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: rgba(0, 0, 0, 0.8);
  border-bottom: 2px solid #ff0000;
  backdrop-filter: blur(10px);
  position: relative;
  z-index: 1;
`;

const Title = styled.h1`
  font-size: 1.8rem;
  font-weight: 700;
  margin: 0;
  color: white;
  text-shadow: 0 0 10px rgba(255, 0, 0, 0.5);
  letter-spacing: 1px;
`;

const Content = styled.div`
  width: 100%;
  max-width: 600px;
  padding: 20px;
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 20px;
  position: relative;
  z-index: 1;
`;

const Card = styled(motion.div)`
  width: 100%;
  background: rgba(255, 255, 255, 0.03);
  border-radius: 20px;
  padding: 25px;
  margin-bottom: 20px;
  border: 2px solid #ff0000;
  backdrop-filter: blur(10px);
  position: relative;
  overflow: hidden;
`;

const CardTitle = styled.h2`
  font-size: 1.4rem;
  margin: 0 0 15px 0;
  color: white;
  text-align: center;
  font-weight: 600;
  letter-spacing: 0.5px;
`;

const CardContent = styled.div`
  color: rgba(255, 255, 255, 0.8);
  text-align: center;
  font-size: 1.1rem;
  line-height: 1.5;
`;

const WelcomeCard = styled(Card)`
  text-align: center;
  background: rgba(255, 0, 0, 0.05);

  h1 {
    font-size: 2.2rem;
    color: white;
    margin-bottom: 15px;
    background: linear-gradient(135deg, #ff0000 0%, #ff4444 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    letter-spacing: 1px;
  }

  p {
    color: rgba(255, 255, 255, 0.9);
    margin-bottom: 25px;
    font-size: 1.2rem;
    line-height: 1.6;
  }
`;

const ActionButton = styled(motion.button)`
  width: 100%;
  background: rgba(255, 0, 0, 0.1);
  border: 2px solid #ff0000;
  border-radius: 15px;
  padding: 20px;
  margin-bottom: 15px;
  color: white;
  font-size: 1.2rem;
  font-weight: 600;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 15px;
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, rgba(255, 0, 0, 0.2), transparent);
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  &:hover {
    transform: translateY(-2px);
    background: rgba(255, 0, 0, 0.15);
    border-color: #ff4444;
    box-shadow: 0 5px 15px rgba(255, 0, 0, 0.2);

    &::before {
      opacity: 1;
    }
  }

  &:active {
    transform: translateY(1px);
  }

  svg {
    font-size: 1.5rem;
    color: #ff0000;
  }
`;

const ConnectButton = styled.div`
  margin-top: 20px;

  :deep(.tc-button) {
    background: linear-gradient(135deg, #ff0000 0%, #ff4444 100%) !important;
    border: none !important;
    border-radius: 30px !important;
    padding: 12px 24px !important;
    font-size: 1.1rem !important;
    font-weight: 600 !important;
    box-shadow: 0 4px 15px rgba(255, 0, 0, 0.3) !important;
    transition: all 0.3s ease !important;
    letter-spacing: 0.5px !important;
    width: 100% !important;

    &:hover {
      transform: translateY(-2px) !important;
      box-shadow: 0 6px 20px rgba(255, 0, 0, 0.4) !important;
    }

    &:active {
      transform: translateY(1px) !important;
    }
  }
`;

const SectionTitle = styled.h2`
  font-size: 1.8rem;
  color: white;
  text-align: center;
  margin: 30px 0 20px;
  font-weight: 600;
  letter-spacing: 1px;
  text-shadow: 0 0 10px rgba(255, 0, 0, 0.3);
`;

function App() {
  const [activeTab, setActiveTab] = useState('home');

  const handleWalletClick = () => {
    setActiveTab('wallet');
  };

  const handleListClick = () => {
    setActiveTab('pokemons');
  };

  const handleSettingsClick = () => {
    setActiveTab('settings');
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'pokemons':
        return (
          <Card
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <CardTitle>My Pokemons</CardTitle>
            <CardContent>
              Your Pokemon collection will appear here
            </CardContent>
          </Card>
        );
      case 'settings':
        return (
          <Card
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <CardTitle>Settings</CardTitle>
            <CardContent>
              App settings
            </CardContent>
          </Card>
        );
      default:
        return (
          <>
            <WelcomeCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3 }}
            >
              <h1>AI Pokemons</h1>
              <p>Connect your TON wallet to start your AI Pokemon adventure</p>
              <ConnectButton>
                <TonConnectButton />
              </ConnectButton>
            </WelcomeCard>

            <SectionTitle>AI Resources</SectionTitle>
            <ActionButton
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: 0.2 }}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              <FaPlus />
              Create Pokemon
            </ActionButton>

            <ActionButton
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: 0.3 }}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              <FaGraduationCap />
              Training
            </ActionButton>

            <ActionButton
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: 0.4 }}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              <FaFistRaised />
              Battle Arena
            </ActionButton>
          </>
        );
    }
  };

  return (
    <AppContainer>
      <Header>
        <Title>AI Pokemons</Title>
      </Header>

      <Content>
        {renderContent()}
      </Content>

      <PokemonMenu
        onWalletClick={handleWalletClick}
        onListClick={handleListClick}
        onSettingsClick={handleSettingsClick}
      />
    </AppContainer>
  );
}

export default App;
