import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import WebApp from '@twa-dev/sdk';

const AppContainer = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background: #1a1a1a;
  color: white;
  perspective: 1000px;
`;

const ComingSoonText = styled(motion.h1)`
  font-size: 2.5rem;
  margin-bottom: 2rem;
  text-align: center;
  text-shadow: 0 0 10px rgba(255, 255, 255, 0.5);
`;

const PokeballContainer = styled(motion.div)`
  width: 120px;
  height: 120px;
  position: relative;
  cursor: pointer;
  transform-style: preserve-3d;
`;

const PokeballTop = styled(motion.div)`
  width: 100%;
  height: 50%;
  background: linear-gradient(45deg, #ff0000, #ff4444);
  border-radius: 60px 60px 0 0;
  border: 5px solid #000;
  position: absolute;
  top: 0;
  transform-origin: bottom;
  box-shadow: inset -5px -5px 15px rgba(0, 0, 0, 0.3);
`;

const PokeballBottom = styled(motion.div)`
  width: 100%;
  height: 50%;
  background: white;
  border-radius: 0 0 60px 60px;
  border: 5px solid #000;
  position: absolute;
  bottom: 0;
  box-shadow: inset 5px 5px 15px rgba(0, 0, 0, 0.1);
`;

const PokeballCenter = styled(motion.div)`
  width: 40px;
  height: 40px;
  background: white;
  border: 5px solid #000;
  border-radius: 50%;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 2;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
`;

const PokeballGlow = styled(motion.div)`
  position: absolute;
  width: 100%;
  height: 100%;
  border-radius: 50%;
  background: radial-gradient(circle, rgba(255,255,255,0.2) 0%, rgba(255,255,255,0) 70%);
  pointer-events: none;
`;

const Notification = styled(motion.div)`
  position: fixed;
  bottom: 20px;
  left: 50%;
  transform: translateX(-50%);
  background: rgba(0, 0, 0, 0.8);
  color: white;
  padding: 10px 20px;
  border-radius: 20px;
  z-index: 1000;
  backdrop-filter: blur(5px);
`;

function App() {
  const [isOpen, setIsOpen] = useState(false);
  const [showNotification, setShowNotification] = useState(false);

  const handlePokeballClick = () => {
    setIsOpen(!isOpen);

    if (window.Telegram && window.Telegram.WebApp) {
      try {
        WebApp.showAlert('Покебол открылся!');
      } catch (error) {
        console.log('Telegram Web App API недоступен');
        showBrowserNotification();
      }
    } else {
      showBrowserNotification();
    }
  };

  const showBrowserNotification = () => {
    setShowNotification(true);
    setTimeout(() => setShowNotification(false), 2000);
  };

  return (
    <AppContainer>
      <AnimatePresence>
        <ComingSoonText
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: 50 }}
          transition={{ duration: 1 }}
        >
          Coming Soon
        </ComingSoonText>
      </AnimatePresence>

      <PokeballContainer
        animate={{
          rotateY: isOpen ? 180 : 0,
          scale: isOpen ? 1.1 : 1,
        }}
        transition={{
          rotateY: { duration: 0.8, ease: "easeInOut" },
          scale: { duration: 0.3 }
        }}
      >
        <PokeballTop
          animate={{
            rotateX: isOpen ? 180 : 0,
          }}
          transition={{
            duration: 0.8,
            ease: "easeInOut"
          }}
        />
        <PokeballBottom />
        <PokeballCenter
          animate={{
            scale: isOpen ? 1.2 : 1,
            opacity: isOpen ? 0.8 : 1,
          }}
          transition={{
            duration: 0.3,
            delay: isOpen ? 0.4 : 0
          }}
        />
        <PokeballGlow
          animate={{
            opacity: isOpen ? 0.5 : 0,
            scale: isOpen ? 1.5 : 1,
          }}
          transition={{
            duration: 0.5,
            delay: isOpen ? 0.3 : 0
          }}
        />
      </PokeballContainer>

      <AnimatePresence>
        {showNotification && (
          <Notification
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 50 }}
            transition={{ duration: 0.3 }}
          >
            Покебол открылся!
          </Notification>
        )}
      </AnimatePresence>
    </AppContainer>
  );
}

export default App;
